const ElementImporter=class{constructor(elements){this.c=document.querySelector('el-import');if(!this.c){console.error("No se encontrÃ³ el elemento 'el-import'");return;}this.e=elements;this.insertStyles();this.replaceElements();}insertStyles() {if(!this.c)return;const styles=this.e.map(c=>c.style).join('');this.c.innerHTML=styles;}replaceElements() {if(!this.c)return;this.e.forEach(c=>{const e=document.querySelectorAll(c.name);e.forEach(o=>{if(c.beforeCreate)c.beforeCreate(o);const n=document.createElement('div');n.innerHTML=c.value;const r=n.firstElementChild;if(r){Array.from(o.attributes).forEach(attr=>{r.setAttribute(attr.name,attr.value);});if(c.value.includes('$content')){r.innerHTML=r.innerHTML.replace('$content', o.innerHTML);}else{r.innerHTML=o.innerHTML;}if(c.onClick){r.addEventListener('click',()=>c.onClick(r));}if(c.onInput&&r.tagName.toLowerCase()==='input') {r.addEventListener('input',()=>c.onInput(r));}o.replaceWith(r);if(c.afterCreate)c.afterCreate(r);}});});}}